function startTime(){var b=new Date();var d=b.getHours();var a=b.getMinutes();a=checkTime(a);d=checkHours(d);document.getElementById("time").innerHTML=d.toString().bold()+" "+a;text=getText(d);var i=["SUN","MON","TUE","WED","THE","FRI","SAT"];var e=i[b.getDay()];var g=b.getDate();var f="&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";document.getElementById("date").innerHTML=g.toString().bold()+f+e.toString().bold();document.getElementById("line").innerHTML="|";var c=setTimeout(function(){startTime()},500)}function checkHours(a){if(a<10){a="0"+a}return a}function checkTime(a){if(a<10){a="0"+a}return a}function getText(a){if(a>=6&&a<12){document.getElementById("dailyText").innerHTML="Good morning, Jesper."}if(a>=12&&a<=18){document.getElementById("dailyText").innerHTML="Good afternoon, Jesper."}if(a>18&&a<=23){document.getElementById("dailyText").innerHTML="Good evening, Jesper."}if(a>=0&&a<6){document.getElementById("dailyText").innerHTML="Good night, Jesper."}};