function startTime(){var b=new Date();var e=b.getHours();var a=b.getMinutes();a=checkTime(a);e=checkHours(e);document.getElementById("time").innerHTML=e.toString().bold()+" "+a;text=getText(e);var j=["SUN","MON","TUE","WED","THE","FRI","SAT"];var f=j[b.getDay()];var g=b.getDate();g=currDay(g);var i="&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";document.getElementById("date").innerHTML=g.toString().bold()+i+f.toString().bold();document.getElementById("line").innerHTML="|";var c=setTimeout(function(){startTime()},500)}function currDay(a){if(a<10){a="0"+a}return a}function checkHours(a){if(a<10){a="0"+a}return a}function checkTime(a){if(a<10){a="0"+a}return a}function getText(a){if(a>=6&&a<12){document.getElementById("dailyText").innerHTML="Good morning, Jesper."}if(a>=12&&a<=18){document.getElementById("dailyText").innerHTML="Good afternoon, Jesper."}if(a>18&&a<=23){document.getElementById("dailyText").innerHTML="Good evening, Jesper."}if(a>=0&&a<6){document.getElementById("dailyText").innerHTML="Good night, Jesper."}};