if("geolocation" in navigator){$(".js-geolocation").show()}else{$(".js-geolocation").hide()}$(".js-geolocation").ready(function(){navigator.geolocation.getCurrentPosition(function(a){loadWeather(a.coords.latitude+","+a.coords.longitude)})});function loadWeather(a,b){$.simpleWeather({location:a,woeid:b,unit:"c",success:function(c){html='<h2><i class="icon-'+c.code+'"></i> '+c.temp+"&deg;</h2>";html+="<ul>"+c.city.bold()+"</ul>";$("#weather").html(html)},error:function(c){$("#weather").html("<p>"+c+"</p>")}})};